!     Written by MD White, PNNL, 28 August, 2013
!
!----------------------Fortran 90 Modules------------------------------!
!
      USE GLB_PAR
      USE SOLTN
      USE POINTE
      USE JACOB
      USE GRID
      USE GLB_PAR
      USE FLUXT
      USE FLUXS
      USE FLUXP
      USE FLUXN
      USE FLUXGC
      USE FDVP
      USE FDVN
      USE FDVG
      USE CONST
      USE BCVP
      USE BCV
!
!----------------------Implicit Double Precision-----------------------!
!
      IMPLICIT REAL*8 (A-H,O-Z)
      IMPLICIT INTEGER (I-N)
!
!----------------------Type Declarations-------------------------------!
!
      REAL*8 BCX(LBCV)
!
!----------------------Executable Lines--------------------------------!
!
      ISUB_LOG = ISUB_LOG+1
      SUB_LOG(ISUB_LOG) = '//BCF_EOR'
!
!---  Zero boundary fluxes  ---
!
      DO 130 NB = 1,NBC
        N = IBCN(NB)
        NPX = NSX(N)
        NPY = NSY(N)
        NPZ = NSZ(N)
        NQX = NSX(N)+1
        NQY = NSY(N)+IFLD
        NQZ = NSZ(N)+IJFLD
        IF( IBCD(NB).EQ.-3 ) THEN
          DO 20 M = 1,ISVF
            WL(M,NPZ) = 0.D+0
            WG(M,NPZ) = 0.D+0
            WN(M,NPZ) = 0.D+0
            WLA(M,NPZ) = 0.D+0
            WLW(M,NPZ) = 0.D+0
            WGW(M,NPZ) = 0.D+0
            WDLA(M,NPZ) = 0.D+0
            WDLW(M,NPZ) = 0.D+0
            WDGW(M,NPZ) = 0.D+0
            WQ(M,NPZ) = 0.D+0
            WS(M,NPZ) = 0.D+0
            WDS(M,NPZ) = 0.D+0
            DO 10 IGC = 1,NGC+2
              WGC(IGC,M,NPZ) = 0.D+0
              WNC(IGC,M,NPZ) = 0.D+0
              WDGC(IGC,M,NPZ) = 0.D+0
              WDNC(IGC,M,NPZ) = 0.D+0
   10       CONTINUE
   20     CONTINUE
        ELSEIF( IBCD(NB).EQ.-2 ) THEN
          DO 40 M = 1,ISVF
            VL(M,NPY) = 0.D+0
            VG(M,NPY) = 0.D+0
            VN(M,NPY) = 0.D+0
            VLA(M,NPY) = 0.D+0
            VLW(M,NPY) = 0.D+0
            VGW(M,NPY) = 0.D+0
            VDLA(M,NPY) = 0.D+0
            VDLW(M,NPY) = 0.D+0
            VDGW(M,NPY) = 0.D+0
            VQ(M,NPY) = 0.D+0
            VS(M,NPY) = 0.D+0
            VDS(M,NPY) = 0.D+0
            DO 30 IGC = 1,NGC+2
              VGC(IGC,M,NPY) = 0.D+0
              VNC(IGC,M,NPY) = 0.D+0
              VDGC(IGC,M,NPY) = 0.D+0
              VDNC(IGC,M,NPY) = 0.D+0
   30       CONTINUE
   40     CONTINUE
        ELSEIF( IBCD(NB).EQ.-1 ) THEN
          DO 60 M = 1,ISVF
            UL(M,NPX) = 0.D+0
            UG(M,NPX) = 0.D+0
            UN(M,NPX) = 0.D+0
            ULA(M,NPX) = 0.D+0
            ULW(M,NPX) = 0.D+0
            UGW(M,NPX) = 0.D+0
            UDLA(M,NPX) = 0.D+0
            UDLW(M,NPX) = 0.D+0
            UDGW(M,NPX) = 0.D+0
            UQ(M,NPX) = 0.D+0
            US(M,NPX) = 0.D+0
            UDS(M,NPX) = 0.D+0
            DO 50 IGC = 1,NGC+2
              UGC(IGC,M,NPX) = 0.D+0
              UNC(IGC,M,NPX) = 0.D+0
              UDGC(IGC,M,NPX) = 0.D+0
              UDNC(IGC,M,NPX) = 0.D+0
   50       CONTINUE
   60     CONTINUE
        ELSEIF( IBCD(NB).EQ.1 ) THEN
          DO 80 M = 1,ISVF
            UL(M,NQX) = 0.D+0
            UG(M,NQX) = 0.D+0
            UN(M,NQX) = 0.D+0
            ULA(M,NQX) = 0.D+0
            ULW(M,NQX) = 0.D+0
            UGW(M,NQX) = 0.D+0
            UDLA(M,NQX) = 0.D+0
            UDLW(M,NQX) = 0.D+0
            UDGW(M,NQX) = 0.D+0
            UQ(M,NQX) = 0.D+0
            US(M,NQX) = 0.D+0
            UDS(M,NQX) = 0.D+0
            DO 70 IGC = 1,NGC+2
              UGC(IGC,M,NQX) = 0.D+0
              UNC(IGC,M,NQX) = 0.D+0
              UDGC(IGC,M,NQX) = 0.D+0
              UDNC(IGC,M,NQX) = 0.D+0
   70       CONTINUE
   80     CONTINUE
        ELSEIF( IBCD(NB).EQ.2 ) THEN
          DO 100 M = 1,ISVF
            VL(M,NQY) = 0.D+0
            VG(M,NQY) = 0.D+0
            VN(M,NQY) = 0.D+0
            VLA(M,NQY) = 0.D+0
            VLW(M,NQY) = 0.D+0
            VGW(M,NQY) = 0.D+0
            VDLA(M,NQY) = 0.D+0
            VDLW(M,NQY) = 0.D+0
            VDGW(M,NQY) = 0.D+0
            VQ(M,NQY) = 0.D+0
            VS(M,NQY) = 0.D+0
            VDS(M,NQY) = 0.D+0
            DO 90 IGC = 1,NGC+2
              VGC(IGC,M,NQY) = 0.D+0
              VNC(IGC,M,NQY) = 0.D+0
              VDGC(IGC,M,NQY) = 0.D+0
              VDNC(IGC,M,NQY) = 0.D+0
   90       CONTINUE
  100     CONTINUE
        ELSEIF( IBCD(NB).EQ.3 ) THEN
          DO 120 M = 1,ISVF
            WL(M,NQZ) = 0.D+0
            WG(M,NQZ) = 0.D+0
            WN(M,NQZ) = 0.D+0
            WLA(M,NQZ) = 0.D+0
            WLW(M,NQZ) = 0.D+0
            WGW(M,NQZ) = 0.D+0
            WDLA(M,NQZ) = 0.D+0
            WDLW(M,NQZ) = 0.D+0
            WDGW(M,NQZ) = 0.D+0
            WQ(M,NQZ) = 0.D+0
            WS(M,NQZ) = 0.D+0
            WDS(M,NQZ) = 0.D+0
            DO 110 IGC = 1,NGC+2
              WGC(IGC,M,NQZ) = 0.D+0
              WNC(IGC,M,NQZ) = 0.D+0
              WDGC(IGC,M,NQZ) = 0.D+0
              WDNC(IGC,M,NQZ) = 0.D+0
  110       CONTINUE
  120     CONTINUE
        ENDIF
  130 CONTINUE
!
!---  Loop over boundary conditions  ---
!
      DO 800 NB = 1,NBC
        TMZ = TM
        IF( NSTEP-NRST.EQ.0 ) TMZ = TMZ*(1.D+0+EPSL)+EPSL
        MB = IBCIN(NB)
        IF( IBCC(NB).EQ.1 ) TMZ = MOD( TM,BC(1,IBCM(NB),MB) )
        IF( TMZ.LE.BC(1,1,MB) ) GOTO 800
        IF( IBCM(NB).EQ.1 ) THEN
          DO 140 N = 1,LBCV
            BCX(N) = BC(N,1,MB)
  140     CONTINUE
        ELSE
          DO 160 M = 2,IBCM(NB)
            IF( TMZ.LE.BC(1,M,MB) ) THEN
             TDBC = (BC(1,M,MB)-BC(1,M-1,MB))
             DTBC = MIN( BC(1,M,MB)-TMZ,DT )
             TFBC = (TMZ-5.D-1*DTBC-BC(1,M-1,MB))/TDBC
             DO 150 N = 1,LBCV
               BCX(N) = BC(N,M-1,MB) + TFBC*(BC(N,M,MB)-BC(N,M-1,MB))
  150        CONTINUE
             GOTO 170
            ENDIF
  160     CONTINUE
          GOTO 800
        ENDIF
  170   CONTINUE
        N = IBCN(NB)
        I = ID(N)
        J = JD(N)
        K = KD(N)
        NPZ = NSZ(N)
        NPY = NSY(N)
        NPX = NSX(N)
        NQX = NPX+1
        NQY = NPY+IFLD
        NQZ = NPZ+IJFLD
        ITFX = MOD(IBCT(2,NB),10)
        ITGX = MOD(IBCT(2,NB),100)/10
        ITLX = MOD(IBCT(2,NB),1000)/100
        ITNX = MOD(IBCT(2,NB),10000)/1000
!
!---    Bottom boundary  ---
!
        IF( IBCD(NB).EQ.-3 ) THEN
!
!---      Fluid flow Neumann boundary  ---
!
          IF( ITFX.EQ.2 .OR. ITFX.EQ.7 .OR. ITFX.EQ.9 ) THEN
            IF( BCX(3).GT.0.D+0 ) THEN
              DO 200 M = 1,ISVF
                MP = MPOS(M)
                IF( ITGX.EQ.0 ) WG(M,NPZ) = SGB(MP,NB)*BCX(3)
                IF( ITLX.EQ.0 ) WL(M,NPZ) = SLB(MP,NB)*BCX(3)
                IF( ITNX.EQ.0 ) WN(M,NPZ) = SNB(MP,NB)*BCX(3)
  200         CONTINUE
            ELSE
              FMGX = RKG(2,N)/VISG(2,N)
              FMLX = RKL(3,2,N)/VISL(2,N)
              FMNX = RKN(2,N)/VISN(2,N)
              SFMX = FMGX + FMLX + FMNX 
              DO 202 M = 1,ISVF
                IF( ITGX.EQ.0 ) WG(M,NPZ) = BCX(3)*FMGX/SFMX
                IF( ITLX.EQ.0 ) WL(M,NPZ) = BCX(3)*FMLX/SFMX
                IF( ITNX.EQ.0 ) WN(M,NPZ) = BCX(3)*FMNX/SFMX
  202         CONTINUE
            ENDIF
            IF( ITLX.EQ.0 .AND. ISLC(45).EQ.0 ) CALL DFFLAB( N,NB )
            IF( ITLX.EQ.0 .AND. ISLC(32).EQ.0 ) CALL DFFLSB( N,NB )
            IF( ITLX.EQ.0 ) CALL DFFLWB( N,NB )
            DO 210 IGC = 1,NGC+2
              IF( IGC.EQ.1 ) THEN
!
!---            Iso-CO2 option  ---
!
                IF( ISLC(45).EQ.0 ) THEN
                  IF( ITGX.EQ.0 ) CALL DFFGCB( IGC,N,NB )
                  IF( ITNX.EQ.0 ) CALL DFFNCB( IGC,N,NB )
                ENDIF
              ELSEIF( IGC.EQ.2 ) THEN
!
!---            Iso-CH4 option  ---
!
                IF( ISLC(64).EQ.0 ) THEN
                  IF( ITGX.EQ.0 ) CALL DFFGCB( IGC,N,NB )
                  IF( ITNX.EQ.0 ) CALL DFFNCB( IGC,N,NB )
                ENDIF
              ELSE
                IF( ITGX.EQ.0 ) CALL DFFGCB( IGC,N,NB )
                IF( ITNX.EQ.0 ) CALL DFFNCB( IGC,N,NB )
              ENDIF
  210       CONTINUE
            IF( ITGX.EQ.0 ) CALL DFFGWB( N,NB )
!
!---      Fluid flow non-zero flux boundary  ---
!
          ELSEIF( ITFX.NE.3 ) THEN
            IF( ITGX.EQ.0 ) CALL DRCVGB( N,NB )
            IF( ITLX.EQ.0 ) CALL DRCVLB( N,NB )
            IF( ITNX.EQ.0 ) CALL DRCVNB( N,NB )
            IF( ITLX.EQ.0 .AND. ISLC(45).EQ.0 ) CALL DFFLAB( N,NB )
            IF( ITLX.EQ.0 .AND. ISLC(32).EQ.0 ) CALL DFFLSB( N,NB )
            IF( ITLX.EQ.0 ) CALL DFFLWB( N,NB )
            DO 220 IGC = 1,NGC+2
              IF( IGC.EQ.1 ) THEN
!
!---            Iso-CO2 option  ---
!
                IF( ISLC(45).EQ.0 ) THEN
                  IF( ITGX.EQ.0 ) CALL DFFGCB( IGC,N,NB )
                  IF( ITNX.EQ.0 ) CALL DFFNCB( IGC,N,NB )
                ENDIF
              ELSEIF( IGC.EQ.2 ) THEN
!
!---            Iso-CH4 option  ---
!
                IF( ISLC(64).EQ.0 ) THEN
                  IF( ITGX.EQ.0 ) CALL DFFGCB( IGC,N,NB )
                  IF( ITNX.EQ.0 ) CALL DFFNCB( IGC,N,NB )
                ENDIF
              ELSE
                IF( ITGX.EQ.0 ) CALL DFFGCB( IGC,N,NB )
                IF( ITNX.EQ.0 ) CALL DFFNCB( IGC,N,NB )
              ENDIF
  220       CONTINUE
            IF( ITGX.EQ.0 ) CALL DFFGWB( N,NB )
          ENDIF
!
!---      Nonisothermal simulations  ---
!
          IF( ISLC(30).EQ.0 )  THEN
!
!---        Energy Neumann  ---
!
            IF( IBCT(1,NB).EQ.2 ) THEN
              DO 230 M = 1,ISVF
                WQ(M,NPZ) = BCX(2)
  230         CONTINUE
!
!---        Energy outflow, inflow, or advective  ---
!
            ELSEIF( IBCT(1,NB).EQ.5 .OR. IBCT(1,NB).EQ.6 .OR.
     &         IBCT(1,NB).EQ.7 ) THEN
              CALL THLB_EOR( N,NB )
              CALL THGB_EOR( N,NB )
              CALL THNB_EOR( N,NB )
!
!---        Energy non-zero flux  ---
!
            ELSEIF( IBCT(1,NB).NE.3 ) THEN
              CALL THDB_EOR( N,NB )
              CALL THLB_EOR( N,NB )
              CALL THGB_EOR( N,NB )
              CALL THNB_EOR( N,NB )
            ENDIF
          ENDIF
!
!---    South boundary  ---
!
        ELSEIF( IBCD(NB).EQ.-2 ) THEN
!
!---      Fluid flow Neumann boundary  ---
!
          IF( ITFX.EQ.2 .OR. ITFX.EQ.7 .OR. ITFX.EQ.9 ) THEN
            IF( BCX(3).GT.0.D+0 ) THEN
              DO 300 M = 1,ISVF
                MP = MPOS(M)
                IF( ITGX.EQ.0 ) VG(M,NPY) = SGB(MP,NB)*BCX(3)
                IF( ITLX.EQ.0 ) VL(M,NPY) = SLB(MP,NB)*BCX(3)
                IF( ITNX.EQ.0 ) VN(M,NPY) = SNB(MP,NB)*BCX(3)
  300         CONTINUE
            ELSE
              FMGX = RKG(2,N)/VISG(2,N)
              FMLX = RKL(2,2,N)/VISL(2,N)
              FMNX = RKN(2,N)/VISN(2,N)
              SFMX = FMGX + FMLX + FMNX 
              DO 302 M = 1,ISVF
                IF( ITGX.EQ.0 ) VG(M,NPY) = BCX(3)*FMGX/SFMX
                IF( ITLX.EQ.0 ) VL(M,NPY) = BCX(3)*FMLX/SFMX
                IF( ITNX.EQ.0 ) VN(M,NPY) = BCX(3)*FMNX/SFMX
  302         CONTINUE
            ENDIF
            IF( ITLX.EQ.0 .AND. ISLC(45).EQ.0 ) CALL DFFLAS( N,NB )
            IF( ITLX.EQ.0 .AND. ISLC(32).EQ.0 ) CALL DFFLSS( N,NB )
