!
      ISUB_LOG = ISUB_LOG+1
      SUB_LOG(ISUB_LOG) = '//CAP_EOR'
!
!---  van Genuchten saturation function
!
      IF( ISCHR(IZN).EQ.1 ) THEN
!
!---    Dual porosity model  ---
!
        IF( ISLC(11).EQ.1 ) THEN
          CALL CAP_FRAC( SGX,SLX,SNX,CPGTX,CPNLX,IZN )
!
!---      Conventional model  ---
!
        ELSE
          CALL CAP_VG( SGX,SLX,SNX,CPGTX,CPNLX,IZN )
        ENDIF
!
