!            Copyright Battelle Memorial Institute, 1996
!                    All Rights Reserved.
!
!----------------------Description-------------------------------------!
!
!     EOR (Enhanced Oil Recovery and Carbon Utilization) Mode
!
!     Load the current time step values into the old time step
!     variables.
!
!----------------------Authors-----------------------------------------!
!
!     Written by Mark D White, PNNL, March 17, 2013
!
!----------------------Fortran 90 Modules------------------------------!
!
      USE GLB_PAR
      USE TRNSPT
      USE SOLTN
      USE REACT
      USE HYST
      USE GRID
      USE FDVT
      USE FDVS
      USE FDVP
      USE FDVN
      USE FDVGC
      USE FDVG
!
!----------------------Implicit Double Precision-----------------------!
!
      IMPLICIT REAL*8 (A-H,O-Z)
      IMPLICIT INTEGER (I-N)
!
!----------------------Executable Lines--------------------------------!
!
      ISUB_LOG = ISUB_LOG+1
      SUB_LOG(ISUB_LOG) = '//LDO_EOR'
!
!---  Assign old time step values  ---
!
      DO 100 N = 1,NFBN
        IF( IXP(N).EQ.0 .OR. IBR(4,N).NE.N ) GOTO 100
        DO 10 IGC = 1,NGC+2
          DFGC(IGC,1,N) = DFGC(IGC,2,N)
          DFNC(IGC,1,N) = DFNC(IGC,2,N)
          TMC(IGC,1,N) = TMC(IGC,2,N)
          XGC(IGC,1,N) = XGC(IGC,2,N)
          XMGC(IGC,1,N) = XMGC(IGC,2,N)
          XLC(IGC,1,N) = XLC(IGC,2,N)
          XMLC(IGC,1,N) = XMLC(IGC,2,N)
          XMNC(IGC,1,N) = XMNC(IGC,2,N)
          XNC(IGC,1,N) = XNC(IGC,2,N)
          ZMC(IGC,1,N) = ZMC(IGC,2,N)
   10   CONTINUE
        ASLMIN(1,N) = MIN( ASL(N),ASLMIN(2,N) )
        ASLMIN(2,N) = ASLMIN(1,N)
        DO M = 1,6
          BETA(M,N) = 0.D+0
        ENDDO
        IBETA(N) = 0
        DFGW(1,N) = DFGW(2,N)
        DFLA(1,N) = DFLA(2,N)
        DFLS(1,N) = DFLS(2,N)
        GNIFT(1,N) = GNIFT(2,N)
!
!---    Isothermal option  ---
!
        IF( ISLC(30).EQ.0 ) THEN
          HG(1,N) = HG(2,N)
          HGA(1,N) = HGA(2,N)
          HGW(1,N) = HGW(2,N)
          HL(1,N) = HL(2,N)
          HLW(1,N) = HLW(2,N)
          HN(1,N) = HN(2,N)
          HSP(1,N) = HSP(2,N)
          UEG(1,N) = UEG(2,N)
          UEL(1,N) = UEL(2,N)
          UEN(1,N) = UEN(2,N)
          THKG(1,N) = THKG(2,N)
          THKL(1,N) = THKL(2,N)
          THKN(1,N) = THKN(2,N)
        ENDIF
        NPHAZ(1,N) = NPHAZ(2,N)
        PERMRF(1,N) = PERMRF(2,N)
        PG(1,N) = PG(2,N)
        PL(1,N) = PL(2,N)
        PN(1,N) = PN(2,N)
        POSM(1,N) = POSM(2,N)
        PSO(1,N) = PSO(2,N)
        PORD(1,N) = PORD(2,N)
        PORT(1,N) = PORT(2,N)
        PVA(1,N) = PVA(2,N)
        PVW(1,N) = PVW(2,N)
        RHOG(1,N) = RHOG(2,N)
        RHOL(1,N) = RHOL(2,N)
        RHOMG(1,N) = RHOMG(2,N)
        RHOML(1,N) = RHOML(2,N)
        RHOMN(1,N) = RHOMN(2,N)
        RHON(1,N) = RHON(2,N)
        RHOSP(1,N) = RHOSP(2,N)
        RKG(1,N) = RKG(2,N)
        RKL(1,1,N) = RKL(1,2,N)
        RKL(2,1,N) = RKL(2,2,N)
        RKL(3,1,N) = RKL(3,2,N)
        RKN(1,N) = RKN(2,N)
        SG(1,N) = SG(2,N)
        SL(1,N) = SL(2,N)
        SN(1,N) = SN(2,N)
