        T(1,N) = T(2,N)
        TMS(1,N) = TMS(2,N)
        TORG(1,N) = TORG(2,N)
        TORL(1,N) = TORL(2,N)
        TORN(1,N) = TORN(2,N)
        VISG(1,N) = VISG(2,N)
        VISL(1,N) = VISL(2,N)
        VISN(1,N) = VISN(2,N)
        XGW(1,N) = XGW(2,N)
        XLA(1,N) = XLA(2,N)
        XLS(1,N) = XLS(2,N)
        XLW(1,N) = XLW(2,N)
        XMGW(1,N) = XMGW(2,N)
        XMLA(1,N) = XMLA(2,N)
        XMLS(1,N) = XMLS(2,N)
        XMLW(1,N) = XMLW(2,N)
        YLS(1,N) = YLS(2,N)
        ZG(1,N) = ZG(2,N)
        ZN(1,N) = ZN(2,N)
        IBETA(N) = 0
        DO 90 NSL = 1,NSOLU
          CO(N,NSL) = C(N,NSL)
   90   CONTINUE
#ifdef ecke
        IF( ISLC(40).EQ.1 ) THEN
          DO 92 NEQ = 1,NEQC+NEQK
            NSL = NEQ + NSOLU 
            CO(N,NSL) = C(N,NSL)
   92     CONTINUE
