          DBETA2X = BETA(3,N)-BETA(2,N)
          ABETAX = 0.D+0
          DO M = 1,3
            ABETAX = ABETAX + BETA(M,N)
          ENDDO
          ABETAX = ABETAX/3.D+0
          SBETAX = 0.D+0
          DO M = 1,3
            SBETAX = SBETAX + (BETA(M,N)-ABETAX)**2
          ENDDO
          SBETAX = SQRT( SBETAX/3.D+0 )
!
!---      Beta previously fixed  ---
!
          IF( IBETA(N).EQ.1 ) THEN
            IBETAX = 1
            BETAX = BETA(3,N)
!
!---      Small variance in beta  ---
!
          ELSEIF( SBETAX.LT.1.D-4 ) THEN
            BETAX = ABETAX
            IBETA(N) = 1
            IBETAX = 1
