  490       CONTINUE
            DO 492 L = 1,LRPLC
              RPLC(L,M) = RPLC(L,IROCK)
  492       CONTINUE
            DO 494 L = 1,LRPNC
              RPNC(L,M) = RPNC(L,IROCK)
  494       CONTINUE
          ENDIF
  496   CONTINUE
      ENDIF
!
!---  Read next rock/soil type or scaling group  ---
!
        IF( IJK.GT.0 ) N = N + NFLD
        IF( N.LT.NROCK ) WRITE(IWR,'(/)')
        GOTO 20
 500  CONTINUE
!
!---  Reset subroutine string sequence  ---
!
      ISUB_LOG = ISUB_LOG-1
!
!---  End of RD3PRP_EOR group ---
!
      RETURN
      END

