!
!----------------------Acknowledgement---------------------------------!
!
!     This software and its documentation were produced with Government
!     support under Contract Number DE-AC06-76RLO-1830 awarded by the
!     United Department of Energy. The Government retains a paid-up
!     non-exclusive, irrevocable worldwide license to reproduce,
!     prepare derivative works, perform publicly and display publicly
!     by or for the Government, including the right to distribute to
!     other Government contractors.
!
!---------------------Copyright Notices--------------------------------!
!
!            Copyright Battelle Memorial Institute, 1996
!                    All Rights Reserved.
!
!----------------------Description-------------------------------------!
!
!     EOR (Enhanced Oil Recovery and Carbon Utilization) Mode
!
!     Gas relative permeability
!
!----------------------Authors-----------------------------------------!
!
!     Written by Mark D White, PNNL, March 17, 2013
!
!----------------------Fortran 90 Modules------------------------------!
!
      USE GLB_PAR
      USE SOLTN
      USE PORMED
!
!----------------------Implicit Double Precision-----------------------!
!
      IMPLICIT REAL*8 (A-H,O-Z)
      IMPLICIT INTEGER (I-N)
!
!----------------------Executable Lines--------------------------------!
!
      ISUB_LOG = ISUB_LOG+1
      SUB_LOG(ISUB_LOG) = '//RKG_EOR'
!
!---  Stone-I 3-phase relative permeability model  ---
!
      IF( IRPN(IZN).EQ.1 ) THEN
!
!---    Corey submodel  ---
!
        IF( MOD(IRPG(IZN),10).EQ.1 ) THEN
          STX = SLX + SNX
          STRX = RPGC(3,IZN)
          SGRX = RPGC(4,IZN)
          ESTX = MIN( MAX( (STX-STRX)/(1.D+0-STRX-SGRX),0.D+0 ),1.D+0 )
          RKGX = RPGC(1,IZN)*((1.D+0-ESTX)**RPGC(2,IZN))
!
!---    LET submodel  ---
!
        ELSEIF( MOD(IRPG(IZN),10).EQ.2 ) THEN
          STX = SLX + SNX
          STRX = RPGC(3,IZN)
          SGRX = RPGC(4,IZN)
          ESTX = MIN( MAX( (STX-STRX)/(1.D+0-STRX-SGRX),0.D+0 ),1.D+0 )
          VARX = (1.D+0-ESTX)**RPGC(2,IZN)
          RKGX = RPGC(1,IZN)*VARX/
     &      (VARX + RPGC(5,IZN)*(ESTX**RPGC(6,IZN)))
!
!---    Unrecognized gas relative permeability submodel  ---
!
        ELSE
          CHMSG = 'Unrecognized Gas Relative Permeability' //
     &        ' Submodel: IRPG(IZN) = '
          INDX = 12
          IMSG = IRPG(IZN)
          CALL WRMSGS( INDX )
        ENDIF
!
!---  Stone-II 3-phase relative permeability model  ---
!
      ELSEIF( IRPN(IZN).EQ.2 ) THEN
!
!---    Corey submodel  ---
!
        IF( MOD(IRPG(IZN),10).EQ.1 ) THEN
          STX = SLX + SNX
          STRX = RPGC(3,IZN)
          SGRX = RPGC(4,IZN)
          ESTX = MIN( MAX( (STX-STRX)/(1.D+0-STRX-SGRX),0.D+0 ),1.D+0 )
          RKGX = RPGC(1,IZN)*((1.D+0-ESTX)**RPGC(2,IZN))
!
!---    LET submodel  ---
!
        ELSEIF( MOD(IRPG(IZN),10).EQ.2 ) THEN
          STX = SLX + SNX
          STRX = RPGC(3,IZN)
          SGRX = RPGC(4,IZN)
          ESTX = MIN( MAX( (STX-STRX)/(1.D+0-STRX-SGRX),0.D+0 ),1.D+0 )
          VARX = (1.D+0-ESTX)**RPGC(2,IZN)
          RKGX = RPGC(1,IZN)*VARX/
     &      (VARX + RPGC(5,IZN)*(ESTX**RPGC(6,IZN)))
!
!---    Unrecognized gas relative permeability submodel  ---
!
        ELSE
          CHMSG = 'Unrecognized Gas Relative Permeability' //
     &        ' Submodel: IRPG(IZN) = '
          INDX = 12
          IMSG = IRPG(IZN)
          CALL WRMSGS( INDX )
        ENDIF
!
!---  Baker 3-phase relative permeability model  ---
!
      ELSEIF( IRPN(IZN).EQ.3 ) THEN
!
!---    Corey submodel  ---
!
        IF( MOD(IRPG(IZN),10).EQ.1 ) THEN
          STX = SLX + SNX
          STRX = RPGC(3,IZN)
          SGRX = RPGC(4,IZN)
          ESTX = MIN( MAX( (STX-STRX)/(1.D+0-STRX-SGRX),0.D+0 ),1.D+0 )
          RKGX = RPGC(1,IZN)*((1.D+0-ESTX)**RPGC(2,IZN))
!
!---    LET submodel  ---
!
        ELSEIF( MOD(IRPG(IZN),10).EQ.2 ) THEN
          STX = SLX + SNX
          STRX = RPGC(3,IZN)
          SGRX = RPGC(4,IZN)
          ESTX = MIN( MAX( (STX-STRX)/(1.D+0-STRX-SGRX),0.D+0 ),1.D+0 )
          VARX = (1.D+0-ESTX)**RPGC(2,IZN)
          RKGX = RPGC(1,IZN)*VARX/
     &      (VARX + RPGC(5,IZN)*(ESTX**RPGC(6,IZN)))
!
!---    Unrecognized gas relative permeability submodel  ---
!
        ELSE
          CHMSG = 'Unrecognized Gas Relative Permeability' //
     &        ' Submodel: IRPG(IZN) = '
          INDX = 12
          IMSG = IRPG(IZN)
          CALL WRMSGS( INDX )
        ENDIF
!
!---  Unrecognized 3-Phase relative permeability model  ---
!
      ELSE
        CHMSG = 'Unrecognized 3-Phase Relative Permeability' //
     &      ' Model: IRPN(IZN) = '
        INDX = 12
        IMSG = IRPN(IZN)
        CALL WRMSGS( INDX )
      ENDIF
      RKGX = MAX( RKGX,0.D+0 )
!
!---  Reset subroutine string sequence  ---
!
      ISUB_LOG = ISUB_LOG-1
!
!---  End of RKG_EOR group  ---
!
      RETURN
      END

