!
        ELSEIF( MOD(NPHAZ(2,N),10).EQ.5 ) THEN
!
!---      Energy equation residual  ---
!
          IF( ISLC(30).EQ.0 ) THEN
            RSDX = ABS(DPT)/TABS
            RSDAVG(IEQT) = RSDAVG(IEQT) + RSDX
            IF( RSDX.GT.RSD(IEQT) ) THEN
