          ENDIF
!
!---      Water mass equation residual  ---
!
          ACP = PORD(2,N)*(RHOL(2,N)*SL(2,N)*XLW(2,N) +
     &      RHOG(2,N)*SG(2,N)*XGW(2,N))*DTI*VOL(N)
          ACP = TMX*DTI*VOL(N)
          IF( NPHAZ(2,N).GT.100 ) THEN
