          ACP = TMX*DTI*VOL(N)
          DO IGC = 1,NGC+2
            IF( ISLC(45).NE.0 .AND. IGC.EQ.1 ) CYCLE
            IF( ISLC(64).NE.0 .AND. IGC.EQ.2 ) CYCLE
