                DPC(IGC) = SIGN( MIN(ABS(DPX),ABS(DPC(IGC))),DPC(IGC) )
                PSO(2,N) = PSO(2,N) + DPC(IGC)
                PSO(2,N) = MIN( PSO(2,N),5.D+8 )
              ENDIF
!
